import React from 'react'
import { nanoid } from 'nanoid'

const AnswerHtml = (props) =>{
    const [stile , setStile] = React.useState(false)
    function ChangeColor(){
       setStile(!stile)
    }
    const styles = {backgroundColor : stile ? 'green' :'white'}
    return (
        <div>
            <h3 className='main-title'>{props.question}</h3>
            <div className='main-flex'>
                <button style={styles} onClick={ChangeColor} className='main-btn'>{props.incorrect_answers[0]}</button>
                <button style={styles} onClick={ChangeColor} className='main-btn'>{props.incorrect_answers[1]}</button>
                <button style={styles} onClick={ChangeColor} className='main-btn'>{props.incorrect_answers[2]}</button>
                <button style={styles} onClick={ChangeColor} className='main-btn'>{props.incorrect_answers[3]}</button>
            </div>
            <hr/>
        </div>
    )
}

export default function GetDataHtml(){

    const [answer , setAnswer] = React.useState([])
    React.useEffect(()=>{
        fetch('https://opentdb.com/api.php?amount=5')
        .then(response => response.json())
        .then(data => {console.log(data)
         setAnswer(() => {return data.results.map(poi =>{return {...poi}
            } )
         })
      })
    },[])
    console.log(answer)
    const [newAnswer , setNewAnswer] = React.useState()
    React.useEffect(()=>{
        const correctAnswer = answer.map(pio => pio.correct_answer )
        const IncorrectArrey = answer.map(pio => pio.incorrect_answers )
        const typeArray = answer.map(pio => pio.type)
        const question = answer.map(pio => pio.question)
        const allAnswer = correctAnswer.map((get , index) => ({
          title:question[index] , correct:get, Incorrec:IncorrectArrey[index] ,isHHeld : false , type:typeArray[index] , id:nanoid(), answers : [get , ...IncorrectArrey[index]]
        }))
        setNewAnswer(allAnswer)
    },[answer])
    console.log(newAnswer)
    
    
    const finalAnswer = answer.map(x=>{
        return <AnswerHtml {...x} key={x.id}
         />
        })
    return(
        <main>{finalAnswer}</main>
    )
}
