import React from 'react'
import { nanoid} from 'nanoid'
import Button from './button'


export default function GetQuizMenu(){
     
    const [quizdata , setData] = React.useState([])
   
      React.useEffect(()=>{
        fetch('https://opentdb.com/api.php?amount=5')
         .then(response => response.json())
         .then(data => setData(data.results.map(x=>x.correct_answer)))
      },[])
      console.log(quizdata)
    //   const correct = quizdata.map(poi => poi.correct_answer)
    //   const incorrect = quizdata.map(poi => poi.incorrect_answers)
    //   const answerArray = quizdata.map((poi,index) =>{ return [poi.incorrect_answers[index],poi.correct_answer[index]]})
    //   console.log(correct)
    //   console.log(incorrect)
    //   console.log(answerArray)

   
   const [idButton , setIdButton] = React.useState(idLoop())

   function getIdisHold(){
    return {id:nanoid() , isHeld:false}
   }

   function idLoop(){
    const idArrayButton = []
    for(let i = 0 ; i< 4 ; i++){
        idArrayButton.push(getIdisHold())
    }
    return idArrayButton
   }
    
    const element = idButton.map(prev =>{        
        return( <Button
             id={prev.id}
              key={prev.id}
               isHeld={prev.isHeld}
                toggle={()=>ChangeColor(prev.id)}
                 />)
    })
    function ChangeColor(id){
        setIdButton(prev=> prev.map(get => {
            return get.id === id ? {...get , isHeld:!get.isHeld} : get
        }))
    }
    return (<div>{element}</div>) 
}
