import React from 'react'
import { nanoid } from 'nanoid'

export default function SetButtons(props){
     const Button = (props) =>{
        // const styles = {backgroundColor : props.correct ?  ' green' : props.isheld && props.value === false ? 'red' :'rgb(221, 224, 238)'}
         const styles = { backgroundColor : props.isheld ? 'green' : props.correct ?  'yellow' : props.isheld === true && props.correct === false ? 'red' :  '#FFF'}
        
        return (
            <div>  
                <button className='main-btn' style={styles} id={props.id} key={props.id} isheld={props.isheld} onClick={props.toggle}>{props.value}</button>             
            </div>
        )
    }
   
    const [buttonArray , setButtonArray ] =React.useState(pushArray())

    function getArray(){
        return ( {id:nanoid() , isheld:false})
    }
    function pushArray(){
        const dice=[]
        for ( let i=0 ; i< 1 ; i++){
            dice.push(getArray())
        }
        return dice
    }
    const buttonFinal = buttonArray.map(get=>{
          return    <>
                     <Button toggle={()=>ChangeColor(get.id)} check={props.check} id={get.id} correct={props.correct} isheld={get.isheld} key={get.id} value={props.value}/>          
          </>
        })
    function ChangeColor(id){
        setButtonArray(prev=> prev.map(get=>{
            return get.id === id ? {...get , isheld:!get.isheld} : get
        })
        )
    }
    return (
    <>
    {buttonFinal}
    </>)
}
