import React from 'react'


export default function GetQuiz(){
    const [ansewr , setAnsewr] = React.useState('')
    fetch('https://opentdb.com/api.php?amount=5')
    .then(response => response.json())
    .then(data => {
       setAnsewr(()=>data.map(poolAnsewr => {
        return poolAnsewr.results
       }))
       
       
    } )
    return (
       <h3>{ansewr.question}</h3>
    )
}
