import React from 'react'
import Button from '../button/setButtons'
import Domanda from './domanda'
import Button2 from '../button/buttonCheck'
import Button3 from '../button/finshButton'

export default function GetQuizMenu(props){    
      // Presa dati dall' API   

    const [quizdata , setData] = React.useState([])
      React.useEffect( () =>{
         fetch('https://opentdb.com/api.php?amount=5')
          .then(response => response.json())
          .then(data=> setData(data.results) )        
      },[]) 
      
      // Presa Array di Domande e Risposte
      const questionHtml = quizdata.map(quiz=> quiz.question)

      const correctArray = quizdata.map(quiz => ({answer : quiz.correct_answer , isCorrect : true}))     
      const incorrectArray = quizdata.map(quiz=> quiz.incorrect_answers.map(incorrect => ({answer : incorrect , isCorrect :false}) ))
      const newArrayAnswer = correctArray.map((quiz,index) =>{
        const incorrect = incorrectArray[index]
        //  QUI MESCOLO L'ARRAY DELLE DOMANDE
        return shuffleArray([quiz , ...incorrect])
      })       
      function shuffleArray(array) {
        for (let i = array.length - 1; i > 0; i--) {
          const j = Math.floor(Math.random() * (i + 1));
          [array[i], array[j]] = [array[j], array[i]];
        }
        return array;
      } 
      const arrayFinale = newArrayAnswer.map((get,index)=> {
        return {question:questionHtml[index] , answer:get}
      })
      console.log(arrayFinale)
        const [correctAnswer , setCorrectAnswer] = React.useState(false)
     function CheckAnswer(){
      setCorrectAnswer(!correctAnswer)
     }
       
      let getAnswers = arrayFinale.map(prev=>{
        return <div className='main-ctn'>
        <Domanda  value={prev.question}/>
        <div className='btn-ctn'>
            {correctAnswer ? <Button value={prev.answer[0].answer}/> : <Button value={prev.answer[0].answer}/>  }
           {correctAnswer ?<Button  value={prev.answer[1].answer}/> :<Button  value={prev.answer[1].answer}/> }
           {correctAnswer ? <Button  value={prev.answer[2].answer}/> : <Button value={prev.answer[2].answer}/>}
          {correctAnswer ? <Button  value={prev.answer[3].answer}/> :<Button value={prev.answer[3].answer}/>}
        </div>
        
           <hr></hr>

</div>
      })
      
      return (
        <main>
           {getAnswers}     
           {correctAnswer ?<p type='submit' className='main-btn'  onClick={props.toggle}>Restart Quiz</p>
           : <p type='submit' className='main-btn'  onClick={()=>CheckAnswer()}>Check Answer</p>}
        </main>
      )
}
