import React from 'react'
import { nanoid } from 'nanoid'

const AnswerHtml = (props) =>{
    const [stile , setStile] = React.useState(false)
    function ChangeColor(){
       setStile(!stile)
    }
    const styles = {backgroundColor : stile ? 'green' :'white'}
    return (
        <div>
            <h3 className='main-title'>{props.title}</h3>
            <div className='main-flex'>
                <button style={styles} onClick={ChangeColor} className='main-btn'>{props.answers.answers[0].text}</button>
                <button style={styles} onClick={ChangeColor} className='main-btn'>{props.answers.answers[0].text}</button>
                <button style={styles} onClick={ChangeColor} className='main-btn'>{props.answers.answers[0].text}</button>
                <button style={styles} onClick={ChangeColor} className='main-btn'>{props.answers.answers[0].text}</button>
            </div>
            <hr/>
        </div>
    )
}

export default function GetDataHtml(){

    const [answer , setAnswer] = React.useState([])
    React.useEffect(()=>{
        fetch('https://opentdb.com/api.php?amount=5')
        .then(response => response.json())
        .then(data => {
            const correctAnswer = data.results.map(pio =>{
                const Correct = pio.correct_answers.map(res => {
                     return {id:nanoid() , res}}
                     
                      )  })
            const IncorrectArrey = data.results.map(pio => pio.incorrect_answers )
            const typeArray = data.results.map(pio => pio.type)
            const question = data.results.map(pio => pio.question)               
            const allAnswer = correctAnswer.map((get , index) => ({
              title:question[index] , correct:get, Incorrec:IncorrectArrey[index] ,isHHeld : false , type:typeArray[index] , id:nanoid(), answers : ''
            }))
         setAnswer(allAnswer)
      })
    },[])
    console.log(answer)
   
   
    
    
    
    const finalAnswer = answer.map(x=>{
        return <AnswerHtml {...x} key={x.id}
         />
        })
    return(
        <main>{finalAnswer}</main>
    )
}
