import React from 'react'
import { nanoid} from 'nanoid'
import Button from '../button/setButtons'
import Domanda from './domanda'

export default function GetQuizMenu(){
     
    const [quizdata , setData] = React.useState([])
      React.useEffect( () =>{
         fetch('https://opentdb.com/api.php?amount=5')
          .then(response => response.json())
          .then(data=> setData(data.results) )
        
      },[]) 
        
      const provetta =  quizdata.map(({correct_answer,incorrect_answers})=>{
        const proviamo = [correct_answer,...incorrect_answers]
        return shuffleArray(proviamo)
      })      
      function shuffleArray(array) {
        for (let i = array.length - 1; i > 0; i--) {
          const j = Math.floor(Math.random() * (i + 1));
          [array[i], array[j]] = [array[j], array[i]];
        }
        return array;
      }  
       const questionHtml = quizdata.map(poi=> poi.question)
      
      const mio = provetta.map(x=> <p>{x[0]}</p>)
     
     
    
      
      return (
        <main>
         <Domanda value={questionHtml[0]} />
         <div className='btn-ctn'>
           <Button value={mio}
            />
           
         </div>
         <hr/>
         <Domanda value={questionHtml[1]} />
         <div className='btn-ctn'> <Button value={provetta[1]} /></div>
         <hr/>
         <Domanda value={questionHtml[2]}/>
         <div className='btn-ctn'> <Button value={provetta[2]} /></div>
         <hr/>
         <Domanda value={questionHtml[3]}/>
         <div className='btn-ctn'> <Button value={provetta[3]} /></div>
         <hr/>
         <Domanda value={questionHtml[4]}/>
        <div className='btn-ctn'> <Button value={provetta} /></div>
        <hr/>
        </main>
      )
}
