import React from 'react'
import { nanoid } from 'nanoid'

const AnswerHtml = (props) =>{
    const styles = {backgroundColor : props.toggle.isHeld ? 'green' :''}
    return (
        <div>
            <h3 className='main-title'>{props.question}</h3>
            <div className='main-flex'>
                <button style={styles} onClick={props.toggle} name='botton1' className='main-btn'>{props.incorrect_answers[0]}<input type='checkbox'/></button>
                <button style={styles} onClick={props.toggle} name='botton2' className='main-btn'>{props.incorrect_answers[1]}</button>
                <button style={styles} onClick={props.toggle} name='botton3' className='main-btn'>{props.incorrect_answers[2]}</button>
                <button style={styles} onClick={props.toggle} name='botton4' className='main-btn'>{props.correct_answer}</button>
            </div>
            <hr/>
        </div>
    )
}


export default function GetDataHtml(){
    const [answer , setAnswer] = React.useState([])
    React.useEffect(()=>{
        fetch('https://opentdb.com/api.php?amount=5')
        .then(response => response.json())
        .then(data => {console.log(data)
         setAnswer(() => {
            return data.results.map(poi =>{
                return {...poi , id:nanoid() , isHeld:false ,}
            } )
         })
      })
    },[])
    console.log(answer)

    const [color , setColor] = React.useState([{name:'botton1' ,isHeld:false},{name:'botton2' ,isHeld:false},{name:'botton3' ,isHeld:false},{name:'botton4' ,isHeld:false}])
    function Toggle(nome){
        setColor(prev => prev.map(p=> {
            return p.name === nome ? {...p , isHeld:!p.isHeld}: p }))
       
    }
    console.log(color)
    
    const NewAnswer = answer.map(x=>{
        return <AnswerHtml {...x} key={x.id} toggle={()=>Toggle(color)}
         />
        })
    return(
        <main>{NewAnswer}</main>
    )
}






