import React from 'react'
import { nanoid } from 'nanoid'
export default function GetDataHtml(){
    const [answer , setAnswer] = React.useState([])
    React.useEffect(()=>{
        fetch('https://opentdb.com/api.php?amount=5')
        .then(response => response.json())
        .then(data => {console.log(data)
         setAnswer(data.results)
      } )
    },[])   
    const NewAnswer = answer.map(x=>{
        return <><AnswerHtml {...x}
        id={nanoid()} /></>
        })
    return(
        <main>{NewAnswer}</main>
    )
}

const AnswerHtml = (props) =>{
    return (
        <div  key={props.id}>
            <h3>{props.question}</h3>
            <div className='main-flex'>
                <button>{props.incorrect_answers[0]}</button>
                <button>{props.incorrect_answers[1]}</button>
                <button>{props.incorrect_answers[2]}</button>
                <button>{props.correct_answer}</button>
            </div>
        </div>
    )
}
