import React from 'react'


const AnswerHtml = (props) =>{
    const [stile , setStile] = React.useState(false)
    const changeColor = () =>{
       setStile(true)
    }
    const styles = {backgroundColor : stile ? 'green' :''}
    return (
        <div>
            <h3 className='main-title'>{props.question}</h3>
            <div className='main-flex'>
                <button style={styles} onClick={changeColor()}  className='main-btn'>{props.incorrect_answers[0]}</button>
                <button style={styles} onClick={changeColor} name='botton2' className='main-btn'>{props.incorrect_answers[1]}</button>
                <button style={styles} onClick={changeColor} name='botton3' className='main-btn'>{props.incorrect_answers[2]}</button>
                <button style={styles} onClick={changeColor} name='botton4' className='main-btn'>{props.correct_answer}</button>
            </div>
            <hr/>
        </div>
    )
}
export default AnswerHtml
