import React from 'react'
import { nanoid} from 'nanoid'
import Button from './button'


export default function GetQuizMenu(){
     
    const [quizdata , setData] = React.useState([])
   
      React.useEffect(()=>{
        fetch('https://opentdb.com/api.php?amount=5')
         .then(response => response.json())
         .then(data => setData(data.results))
      },[])
      console.log(quizdata)
        
      const provetta = quizdata.map(({correct_answer,incorrect_answers,question})=>{
        return [correct_answer,...incorrect_answers,question]
      })
      console.log(provetta)

      const allAnswers = quizdata.map(({correct_answer, incorrect_answers}) => {
        const allOptions = [correct_answer, ...incorrect_answers];
        return shuffleArray(allOptions); // Qui puoi utilizzare una funzione per mescolare l'ordine delle risposte in modo casuale se vuoi.
      });
      function shuffleArray(array) {
        for (let i = array.length - 1; i > 0; i--) {
          const j = Math.floor(Math.random() * (i + 1));
          [array[i], array[j]] = [array[j], array[i]];
        }
        return array;
      }
     
    
    
    
   
   const [idButton , setIdButton] = React.useState(idLoop())

   function getIdisHold(){
    return {id:nanoid() , isHeld:false}
   }

   function idLoop(){
    const idArrayButton = []
    for(let i = 0 ; i< 4 ; i++){
        idArrayButton.push(getIdisHold())
    }
    return idArrayButton
   }
    
    const element = idButton.map(prev =>{        
        return( <Button
             id={prev.id}
              key={prev.id}
               isHeld={prev.isHeld}
                toggle={()=>ChangeColor(prev.id)}
                 />)
    })
    function ChangeColor(id){
        setIdButton(prev=> prev.map(get => {
            return get.id === id ? {...get , isHeld:!get.isHeld} : get
        }))
    }
    return (<div>{element}</div>) 
}
