import React from 'react'
import { nanoid} from 'nanoid'
import Button from './button'
import Domanda from './domanda'

export default function GetQuizMenu(){
     
    const [quizdata , setData] = React.useState([])
      React.useEffect( () =>{
        async function getDataValue(){
          const response = await  fetch('https://opentdb.com/api.php?amount=5')
          const data = await response.json()
          setData(data.results)
        }
        getDataValue()
      },[]) 
        
      const provetta =  quizdata.map(({correct_answer,incorrect_answers})=>{
        const proviamo = [correct_answer,...incorrect_answers]
        return shuffleArray(proviamo)
      })      
      async function shuffleArray(array) {
        for (let i = array.length - 1; i > 0; i--) {
          const j = Math.floor(Math.random() * (i + 1));
          [array[i], array[j]] = [array[j], array[i]];
        }
        return array;
      }  
       const questionHtml = quizdata.map(poi=> poi.question)
       const finale = provetta.map(poi=>{

        return  {questionHtml , poi}
      }
      )
      console.log(finale)
   
}
