import React from 'react'
import { nanoid } from 'nanoid'

const AnswerHtml = (props) =>{
    const styles = {backgroundColor : props.isHeld ? 'green' :''}
    return (
        <div>
            <h3 className='main-title'>{props.question}</h3>
            <div className='main-flex'>
                <button onClick={props.toggle} className='main-btn'>{props.incorrect_answers[0]}</button>
                <button onClick={props.toggle} className='main-btn'>{props.incorrect_answers[1]}</button>
                <button onClick={props.toggle} className='main-btn'>{props.incorrect_answers[2]}</button>
                <button onClick={props.toggle} className='main-btn'>{props.correct_answer}</button>
            </div>
            <hr/>
        </div>
    )
}


export default function GetDataHtml(){
    const [answer , setAnswer] = React.useState([])
    React.useEffect(()=>{
        fetch('https://opentdb.com/api.php?amount=5')
        .then(response => response.json())
        .then(data => {console.log(data)
         setAnswer(() => {
            return data.results.map(poi =>{
                return {...poi , id:nanoid() , isHeld:false ,}
            } )
         })
      })
    },[])
    console.log(answer)
    function Toggle(id){
        setAnswer(prev => prev.map(pre => {
            return pre.id === id ? {...pre  , isHeld:!pre.isHeld} : pre   })
           
        )
    }
    
    
    const NewAnswer = answer.map(x=>{
        return <AnswerHtml {...x} key={x.id} toggle={()=>Toggle(x.id)}
         />
        })
    return(
        <main>{NewAnswer}</main>
    )
}
