import React from 'react'
import { nanoid } from 'nanoid'
import Button from './button'

export default function SetButtons(props){
    console.log(props)
    const [buttonArray , setButtonArray ] =React.useState(pushArray())

    function getArray(){
        return ( {id:nanoid() , isheld:false})
    }
    function pushArray(){
        const dice=[]
        for ( let i=0 ; i< props.length; i++){
            dice.push(getArray())
        }
        return dice
    }
    const buttonFinal = buttonArray.map(get=>{
          return <Button
                       toggle={()=>ChangeColor(get.id)}
                        id={get.id} isheld={get.isheld}
                         key={get.id}
                         value={props.value}
                          />
        })
    function ChangeColor(id){
        setButtonArray(prev=> prev.map(get=>{
            return get.id === id ? {...get , isheld:!get.isheld} : get
        })
        )
    }
    return (
    <>
    {buttonFinal}
    </>)
}
