import React from 'react'
import { nanoid } from 'nanoid'

const AnswerHtml = (props) =>{
    function GetButtons(){
        const buttons = []
        for (let i = 0 ; i < props.answers.length ; i++){
            const buttonId= nanoid()
            console.log(buttonId)
            const button = ( <button style={styles} onClick={ChangeColor} key={buttonId} id={buttonId} className='main-btn'>{props.correct.value}</button>)
            buttons.push(button)
        }
        return buttons
    }
    const [stile , setStile] = React.useState(false)
    function ChangeColor(){
       setStile(!stile)
    }
    const styles = {backgroundColor : stile ? 'green' :'white'}
    return (
        <div>
            <h3 className='main-title'>{props.title}</h3>
            <div className='main-flex'>
                <GetButtons />
                <button style={styles} onClick={ChangeColor} className='main-btn'>{props.correct.value}</button>
                <button style={styles} onClick={ChangeColor} className='main-btn'>{props.answers[1]}</button>
                <button style={styles} onClick={ChangeColor} className='main-btn'>{props.answers[2]}</button>
                <button style={styles} onClick={ChangeColor} className='main-btn'>{props.answers[3]}</button>
            </div>
            <hr/>
        </div>
    )
}

export default function GetDataHtml(){

    const [answer , setAnswer] = React.useState([])
    React.useEffect(()=>{
        fetch('https://opentdb.com/api.php?amount=5')
        .then(response => response.json())
        .then(data => {        
            const correctAnswer = data.results.map(pio => pio.correct_answer)
            const IncorrectArrey = data.results.map(pio => pio.incorrect_answers )
            const newId = nanoid()
            const gyui = IncorrectArrey.map(pio => ({Incorrect:pio , id:newId}))
            console.log(gyui)
            const typeArray = data.results.map(pio => pio.type)
            const question = data.results.map(pio => pio.question)          
            const allAnswer = correctAnswer.map((get , index) => ({
              title:question[index] , correct:{value:get , id:newId}, Incorrec:[IncorrectArrey[index].map((pio) => ({id:newId , value:pio}))] ,isHHeld : false , type:typeArray[index] , id:newId, answers : [...IncorrectArrey[index] , correctAnswer[index]]
            }))
         setAnswer(allAnswer)
      })
    },[])
    console.log(answer)
   
   
    
    
    
    const finalAnswer = answer.map(x=>{
        return <AnswerHtml {...x} key={x.id}
         />
        })
    return(
        <main>{finalAnswer}</main>
    )
}
