import React from 'react'


export default function GetQuiz(){
    const [ansewr , setAnsewr] = React.useState([])
   
      React.useEffect(()=>{
          fetch('https://opentdb.com/api.php?amount=5')
          .then(response => response.json())
          .then(data => {console.log(data)
             setAnsewr(data.results)
      } )
      },[])
     const ansewrHtml = ansewr.forEach(prev=>{
        return <main>
                  <h3>{prev.question}</h3>
               </main>
     })     
    return (
       {ansewrHtml}
    )
}
