import React from 'react'
import { nanoid} from 'nanoid'
import Button from './button'

// function getData(){
//     React.useEffect(()=>{
//         fetch('https://opentdb.com/api.php?amount=5')
//         .then(response => response.json())
//         .then(data => console.log(data))
//      },[])
// }


export default function GetQuizMenu(){
   
     const [data , setData ] = React.useState('')
   const [idButton , setIdButton] = React.useState(idLoop())

   function getIdisHold(){
    return {id:nanoid() , isHeld:false}
   }

   function idLoop(){
    const idArrayButton = []
    for(let i = 0 ; i< 4 ; i++){
        idArrayButton.push(getIdisHold())
    }
    return idArrayButton
   }
    
    const element = idButton.map(prev =>{        
        return( <Button id={prev.id} key={prev.id} isHeld={prev.isHeld} toggle={()=>ChangeColor(prev.id)} />)
    } 
    )
    function ChangeColor(id){
        console.log(id)
        setIdButton(prev=> prev.map(get => {
            return get.id === id ? {...get , isHeld:!get.isHeld} : get
        }))
    }
    return (
       <div>
           {element}
       </div>
    )
   
}
