import React from 'react'


export default function Button(props){
    console.log(props)
     const styles = { backgroundColor : props.isHeld ?  ' green' : '#FFF'}
    return (
        <div>
              <h2  className='main' key={props.id} onClick={props.toggle}>{props.value}</h2>  
            <button style={styles} className='main-btn' key={props.id} onClick={props.toggle}>{props.answer1}</button>
            <button style={styles} className='main-btn' key={props.id} onClick={props.toggle}>{props.answer2}</button>
            <button style={styles} className='main-btn' key={props.id} onClick={props.toggle}>{props.answer3}</button>
            <button style={styles} className='main-btn' key={props.id} onClick={props.toggle}>{props.answer4}</button>

        </div>
    )
}
