import React from 'react'
// import { nanoid} from 'nanoid'
// import Button from './button'
import Domanda from './domanda'

export default function GetQuizMenu(){
     
    const [quizdata , setData] = React.useState([])
    const [provetta , setPtovetta] = React.useState('')
      React.useEffect( () =>{
        async function getDataValue(){
          const response = await  fetch('https://opentdb.com/api.php?amount=5')
          const data = await response.json()
          setData(data.results)
          setPtovetta(()=>{

            quizdata.map(({correct_answer,incorrect_answers})=>{
             const proviamo = [correct_answer,...incorrect_answers]
             return shuffleArray(proviamo)
           })      
          })
          function shuffleArray(array) {
            for (let i = array.length - 1; i > 0; i--) {
              const j = Math.floor(Math.random() * (i + 1));
              [array[i], array[j]] = [array[j], array[i]];
            }
            return array;
          }
        }
        getDataValue()
      },[])
   
        
     
      const questionHtml = quizdata.map(poi=> poi.question)
   
    console.log(provetta)
 
    return (
    <div>
        <Domanda
         isHeld={quizdata.isHeld}       
          answer={provetta[0]}
           value={questionHtml[0]} />
        <Domanda
         isHeld={quizdata.isHeld}      
          answer={provetta[1]}
           value={questionHtml[1]} />
        <Domanda
         isHeld={quizdata.isHeld}      
          answer={provetta[2]}
           value={questionHtml[2]} />
        <Domanda
         isHeld={quizdata.isHeld}     
          answer={provetta[3]}
           value={questionHtml[3]} />
        <Domanda
         isHeld={quizdata.isHeld}        
          answer={provetta[4]}
           value={questionHtml[4]} />
    
    </div>
    
    ) 
}
