import React from 'react'
import Start from './component/Sstart'
import Quiz from './component/quiz'
export default function App(){
   const [begin , setBegin] = React.useState(false)
    function GetStartGame(){
       setBegin(true)
    }
    function Prova() {
        const [selectedAnswer, setSelectedAnswer] = React.useState(null);
        const questions = [
          {
            question: 'Qual è il capitale dell\'Italia?',
            answers: ['Roma', 'Milano', 'Napoli'],
          },
          {
            question: 'Quale è il fiume più lungo del mondo?',
            answers: ['Nilo', 'Amazzoni', 'Mississippi'],
          },
        ];

        const handleSelectAnswer = (index) => {
          setSelectedAnswer(index);
        };

        return (
          <div>
            {questions.map((q, index) => (
              <div key={index}>
                <h3>{q.question}</h3>
                {q.answers.map((answer, answerIndex) => (
                  <button
                    key={answerIndex}
                    style={{ backgroundColor: selectedAnswer === answerIndex ? 'green' : 'white' }}
                    onClick={() => handleSelectAnswer(answerIndex)}
                  >
                    {answer}
                  </button>
                ))}
              </div>
            ))}
          </div>
        );
      }



    return(
        <div className='root-ctn'>
            <img className='img-blu' src='./images/blob.png' alt='sfondo-blu'/>
            <img className='img-yellow' src='./images/bloby.png' alt='sfondo-giallo'/>
            {begin === false && <Start toggle={GetStartGame} />}
            {begin && <Quiz />}
            <Prova />
        </div>
    )
}
