import React, { useState } from 'react';
import { nanoid } from 'nanoid';

export default function SetButtons(props) {
    const [isheld, setIsheld] = useState(false);
  const [buttonArray, setButtonArray] = useState(pushArray());

  function getArray() {
    return { id: nanoid(), isheld: isheld };
  }

  function pushArray() {
    const dice = [];
    for (let i = 0; i < 1; i++) {
      dice.push(getArray());
    }
    return dice;
  }

  function Button({ value }) {
    

    const handleClick = () => {
      setIsheld(!isheld);
    };
    const proviamo = {backgroundColor : props.correct ? 'green' : isheld ? 'green' : 'rgb(214, 51, 51,0.5)'}
    const styles = { backgroundColor: isheld ? 'green' : props.correct ? 'green' : props.correct === false || props.isheld ? 'rgb(214, 51, 51,0.5)' : '#FFF' };
    const stylesCheck = {
      backgroundColor: props.correct ? 'green' : isheld ? 'rgb(214, 51, 51,0.5)' : 'rgb(221, 224, 238)',
    };

    return (
      <div>
        {props.check ? (
          <button className="main-btn" disabled style={proviamo} isheld={props.isheld} onClick={handleClick}>
            {value}
          </button>
        ) : (
          <button className="main-btn" style={proviamo} onClick={handleClick}>
            {value}
          </button>
        )}
      </div>
    );
  }

  const buttonFinal = buttonArray.map((get) => (    
    <Button key={get.id} check={props.check} isheld={get.isheld} value={props.value} />
    
  ));

  return <>{buttonFinal}</>;
}
