import React from 'react'


export default function GetQuiz(){
    const [ansewr , setAnsewr] = React.useState([])
      React.useEffect(()=>{
          fetch('https://opentdb.com/api.php?amount=5')
          .then(response => response.json())
          .then(data => {console.log(data)
           setAnsewr(data.results)
        } )
    },[])
    const newAnswer = ansewr.map(x => {
        return <>
                 <AnswerHtml {...x} />
               </>
    })
    return (
        <main>{newAnswer}</main>
    )
}


 const AnswerHtml = (props)=>{
      return (
        <div>
            <h3>{props.question}</h3>
            <h3></h3>
        </div>
      )

 }
