import React from 'react'
import { nanoid } from 'nanoid'

const AnswerHtml = (props) =>{
   
    function Toggle(nome){
        const [color , setColor] = React.useState([{name:'botton1' ,isHeld:false},{name:'botton2' ,isHeld:false},{name:'botton3' ,isHeld:false},{name:'botton4' ,isHeld:false}])
        setColor(prev => prev.map(p=> {
            return p.name === nome ? {...p , isHeld:!p.isHeld}: p }))
       
    }
    console.log(color)
    const styles = {backgroundColor : color.isHeld ? 'green' :''}
    return (
        <div>
            <h3 className='main-title'>{props.question}</h3>
            <div className='main-flex'>
                <button style={styles} onClick={Toggle} name='botton1' className='main-btn'>{props.incorrect_answers[0]}</button>
                <button style={styles} onClick={Toggle} name='botton2' className='main-btn'>{props.incorrect_answers[1]}</button>
                <button style={styles} onClick={Toggle} name='botton3' className='main-btn'>{props.incorrect_answers[2]}</button>
                <button style={styles} onClick={Toggle} name='botton4' className='main-btn'>{props.correct_answer}</button>
            </div>
            <hr/>
        </div>
    )
}


export default function GetDataHtml(){
    const [answer , setAnswer] = React.useState([])
    React.useEffect(()=>{
        fetch('https://opentdb.com/api.php?amount=5')
        .then(response => response.json())
        .then(data => {console.log(data)
         setAnswer(() => {
            return data.results.map(poi =>{
                return {...poi , id:nanoid() , isHeld:false ,}
            } )
         })
      })
    },[])
    console.log(answer)

   
    
    const NewAnswer = answer.map(x=>{
        return <AnswerHtml {...x} key={x.id} 
         />
        })
    return(
        <main>{NewAnswer}</main>
    )
}






