import React from 'react'
import Button from '../button/setButtons'
import Domanda from './domanda'
import Button2 from '../button/buttonCheck'
import Button3 from '../button/finshButton'

export default function GetQuizMenu(){    
      // Presa dati dall' API   

    const [quizdata , setData] = React.useState([])
      React.useEffect( () =>{
         fetch('https://opentdb.com/api.php?amount=5')
          .then(response => response.json())
          .then(data=> setData(data.results) )
        
      },[]) 
      
      const correctArray = quizdata.map(quiz => {
        return [{answer : quiz.correct_answer , isCorrect : true}]
      })
      console.log(correctArray)
      const handleIncorrectArray = quizdata.map(quiz => {
        return [quiz.incorrect_answers]
      })
      console.log(handleIncorrectArray)
      const incorrectArray = handleIncorrectArray.map(quiz=>{
        return {answer : quiz , isCorrect : false}
      })
      console.log(incorrectArray)
      // Presa Array di Domande e Risposte
      
      const questionHtml = quizdata.map(quiz=> quiz.question)
      const provetta =  quizdata.map(({correct_answer,incorrect_answers})=>{ 
        const proviamo = [correct_answer  ,...incorrect_answers]
        //  QUI MESCOLO L'ARRAY DELLE DOMANDE
        return shuffleArray(proviamo)
      })
      function shuffleArray(array) {
        for (let i = array.length - 1; i > 0; i--) {
          const j = Math.floor(Math.random() * (i + 1));
          [array[i], array[j]] = [array[j], array[i]];
        }
        return array;
      } 
      const arrayFinale = provetta.map((get,index)=> {
        return {question:questionHtml[index] , answer:get}
      })
      
        const [correctAnswer , setCorrectAnswer] = React.useState(false)
     function CheckAnswer(){
      setCorrectAnswer(!correctAnswer) 
     }
       
      let getAnswers = arrayFinale.map(prev=>{
        return <div className='main-ctn'>
        <Domanda  value={prev.question}/>
        <div className='btn-ctn'>
            {correctAnswer ? <Button3  value={prev.answer[0]}/> : <Button value={prev.answer[0]}/>  }
           {correctAnswer ?<Button2  value={prev.answer[1]}/> :<Button  value={prev.answer[1]}/> }
           {correctAnswer ? <Button3  value={prev.answer[2]}/> : <Button value={prev.answer[2]}/>}
          {correctAnswer ? <Button3  value={prev.answer[3]}/> :<Button value={prev.answer[3]}/>}
        </div>
        
           <hr></hr>

</div>
      })
      
      return (
        <main>
           {getAnswers}     
           <p type='submit' className='main-btn'  onClick={()=>CheckAnswer()}>{correctAnswer ? 'Restart Quiz':'Check Answer'}</p>
        </main>
      )
}
