import React from 'react'
import { nanoid } from 'nanoid'

const AnswerHtml = (props) =>{

    const buttons = []
    function GetButtons(){
        for (let i = 0 ; i < props.answers.length ; i++){
            const buttonId= {id:nanoid() , value:props.answers[i] , isHeld:false}
            const button = ( <button style={styles} onClick={()=>ChangeColor(button.id)} key={buttonId.id} id={buttonId.id} className='main-btn'>{props.answers[i]}</button>)
            buttons.push(buttonId)
        }
        return buttons
    }
    
    const [stile , setStile] = React.useState(buttons)
    function ChangeColor(id){
       console.log(stile)
       
    }
    const styles = {backgroundColor : stile ? 'green' :'white'}
    return (
        <div>
            <h3 className='main-title'>{props.title}</h3>
            <div className='main-flex'>
                <GetButtons key={nanoid()} />
            </div>
            <hr/>
        </div>
    )
}

export default function GetDataHtml(){

    const [answer , setAnswer] = React.useState([])
    React.useEffect(()=>{
        fetch('https://opentdb.com/api.php?amount=5')
        .then(response => response.json())
        .then(data => {        
            const correctAnswer = data.results.map(pio => pio.correct_answer)
            const IncorrectArrey = data.results.map(pio => pio.incorrect_answers )
            const newId = nanoid()
            const typeArray = data.results.map(pio => pio.type)
            const question = data.results.map(pio => pio.question)          
            const allAnswer = correctAnswer.map((get , index) => ({
              title:question[index] , correct:{value:get , id:newId}, Incorrec:[IncorrectArrey[index].map((pio) => ({id:newId , value:pio}))] ,isHHeld : false , type:typeArray[index] , id:newId, answers : [...IncorrectArrey[index] , correctAnswer[index]]
            }))
         setAnswer(allAnswer)
      })
    },[])
    console.log(answer)
   
   
    
    
    
    const finalAnswer = answer.map(x=>{
        return <AnswerHtml {...x} key={nanoid()}
         />
        })
    return(
        <main>{finalAnswer}</main>
    )
}
