import React from 'react'
import { nanoid } from 'nanoid'
import Button from './button'

export default function SetButtons(){
    const [buttonArray , setButtonArray ] =React.useState(pushArray())

    function getArray(){
        return ( {id:nanoid() , isHeld:false})
    }
    function pushArray(){
        const dice=[]
        for ( let i=0 ; i< 4 ; i++){
            dice.push(getArray())
        }
        return dice
    }
    const buttonFinal = buttonArray.map(get=>{
          return <Button
                       toggle={()=>ChangeColor(get.id)}
                        id={get.id} isHeld={get.isHeld}
                         key={get.id}
                          />
        })
    function ChangeColor(id){
        setButtonArray(prev=> prev.map(get=>{

            return get.id === id ? {...get , isHeld:!get.isHeld} : get
        })
        )
    }
    return (
    <>
    {buttonFinal}
    </>)
}
