import React from 'react'


export default function Button(props){
   
     const styles = { backgroundColor : props.isheld ?  ' green' : '#FFF'}

    return (
        <div>  
            <button className='main-btn' style={styles} id={props.id} key={props.id} isheld={props.isheld} onClick={props.toggle}>{props.value}</button>             
        </div>
    )
}
