import React from 'react'
import { nanoid } from 'nanoid'

const AnswerHtml = (props) =>{

    const buttons = []
    function GetButtons(){
        for (let i = 0 ; i < props.answers.length ; i++){
            const styles = {backgroundColor : props.isHeld ? 'green' :'white'}
            const button = ( <button style={styles} onClick={props.toggle} key={nanoid()} value={props.isHeld} id={props.id} className='main-btn'>{props.answers[i]}</button>)
            buttons.push(button)
        }
        return buttons
    }
    // function getId(){
    //     const idNumber = []
    //     for(let i =0 ; i < props.answers.length ; i++){
    //         const finalId = nanoid() 
    //         idNumber.push(finalId)
    //     }
    //     return idNumber
    // }
    return (
        <div>
            <h3 className='main-title'>{props.title}</h3>
            <div className='main-flex'>
                {GetButtons()}
            </div>
            <hr/>
        </div>
    )
}

export default function GetDataHtml(){

    const [answer , setAnswer] = React.useState([])
    React.useEffect(()=>{
        fetch('https://opentdb.com/api.php?amount=5')
        .then(response => response.json())
        .then(data => {        
            const newId = nanoid()
            const correctAnswer = data.results.map(pio => pio.correct_answer)
            const incorrectArrey = data.results.map((pio) => pio.incorrect_answers)
            const typeArray = data.results.map(pio => pio.type)
            const question = data.results.map(pio => pio.question)          
            const allAnswer = correctAnswer.map((get , index) => ({
              title:question[index] , correct:{value:get , id:newId}, incorrec:[incorrectArrey[index].map((pio) => ({id:newId , value:pio}))] ,isHeld : false , type:typeArray[index] , id:newId, answers :[...incorrectArrey[index],correctAnswer[index]]
            }))
         setAnswer(allAnswer)
      })
    },[])
    console.log(answer) 
    
    const finalAnswer = answer.map(x=>{
        console.log(x)
        return <AnswerHtml {...x} key={nanoid()} toggle={()=>ChangeColor(x.answers)} id={x.answers}
         />
        })
        function ChangeColor(id) {
           setAnswer(prev => prev.map(newPrev => {
            for(let i =0 ; i < newPrev.length;i++){}
            return newPrev.id === id ? {...newPrev , isHeld:!newPrev.isHeld} : newPrev 
           }))
        }
    return(
        <main>{finalAnswer}</main>
    )
}
