import React from 'react'
import {nanoid} from 'nanoid'

export default function Button(props){
    console.log(props)
     const styles = { backgroundColor : props.isHeld ?  ' green' : '#FFF'}   
    
    const Button =  <button style={styles} className='main-btn'  onClick={props.toggle}>proviamo</button>        
        
        const [idButton , setIdButton] = React.useState(idLoop())

   function getIdisHold(){
    return {id:nanoid() , isHeld:false}
   }

   function idLoop(){
    const idArrayButton = []
    for(let i = 0 ; i< 4 ; i++){
        idArrayButton.push(getIdisHold())
    }
    return idArrayButton
   }
    
    const element = idButton.map(prev =>{        
        return(   
          <Button id={nanoid()}  key={prev.id}  isHeld={prev.isHeld} toggle={()=>ChangeColor(prev.id)}  />             
             
            )
    })
    function ChangeColor(id){
        setIdButton(prev=> prev.map(get => {
            return get.id === id ? {...get , isHeld:!get.isHeld} : get
        }))
    }
    return (
    <div>
        {element}
    </div>
    
    ) 
}
