import React from 'react'
import { nanoid } from 'nanoid'


export default function GetDataHtml(){
    const [answer , setAnswer] = React.useState([])
    React.useEffect(()=>{
        fetch('https://opentdb.com/api.php?amount=5')
        .then(response => response.json())
        .then(data => {console.log(data)
         setAnswer(() => {
            return data.results.map(poi =>{
                return {...poi , id:nanoid()}
            } )
         })
      })
    },[])
    console.log(answer)
    // const [answercomplete , setAnswerComplete] = React.useState(NuovaProva())
    // function NuovaProva(){
    //   const  newArray = []
    //     for(let i = 0 ; i < 5 ; i++){
    //         return {question :'' ,id:nanoid() ,isHeld:false , correct : '' , incorrect:''}
    //     }
    //     return newArray
    // }

    // console.log(answercomplete)
    
    const NewAnswer = answer.map(x=>{
        return <AnswerHtml {...x} 
         />
        })
    return(
        <main>{NewAnswer}</main>
    )
}

const AnswerHtml = (props) =>{
   
    return (
        <div>
            <h3 className='main-title'>{props.question}</h3>
            <div className='main-flex'>
                <button className='main-btn'>{props.incorrect_answers[0]}</button>
                <button className='main-btn'>{props.incorrect_answers[1]}</button>
                <button className='main-btn'>{props.incorrect_answers[2]}</button>
                <button className='main-btn'>{props.correct_answer}</button>
            </div>
            <hr/>
        </div>
    )
}
