import React from 'react'
import { nanoid} from 'nanoid'
// import Button from './button'
import Domanda from './domanda'

export default function GetQuizMenu(){
     
    const [quizdata , setData] = React.useState([])
   
      React.useEffect(()=>{
        fetch('https://opentdb.com/api.php?amount=5')
         .then(response => response.json())
         .then(data => setData(data.results))
      },[])
    //   console.log(quizdata)
        
      const provetta = quizdata.map(({correct_answer,incorrect_answers})=>{
        const proviamo = [correct_answer,...incorrect_answers]
        return shuffleArray(proviamo)
      })      
      function shuffleArray(array) {
        for (let i = array.length - 1; i > 0; i--) {
          const j = Math.floor(Math.random() * (i + 1));
          [array[i], array[j]] = [array[j], array[i]];
        }
        return array;
      }
      const questionHtml = quizdata.map(poi=> poi.question)
   
   const [idButton , setIdButton] = React.useState(idLoop())

   function getIdisHold(){
    return {id:nanoid() , isHeld:false}
   }

   function idLoop(){
    const idArrayButton = []
    for(let i = 0 ; i< 5 ; i++){
        idArrayButton.push(getIdisHold())
    }
    return idArrayButton
   }
    console.log(provetta)
    // function ChangeColor(id){
    //     setIdButton(prev=> prev.map(get => {
    //         return get.id === id ? {...get , isHeld:!get.isHeld} : get
    //     }))
    // }
    return (
    <div>
        <Domanda isHeld={quizdata.isHeld}
        //  toggle={()=>ChangeColor(idButton.id[0])}
          answer={provetta[0]}
           value={questionHtml[0]} />
        <Domanda isHeld={quizdata.isHeld}
        //  toggle={()=>ChangeColor()}
          answer={provetta[1]}
           value={questionHtml[1]} />
        <Domanda isHeld={quizdata.isHeld}
        //  toggle={()=>ChangeColor()}
          answer={provetta[2]}
           value={questionHtml[2]} />
        <Domanda isHeld={quizdata.isHeld}
        //  toggle={()=>ChangeColor()}
          answer={provetta[3]}
           value={questionHtml[3]} />
        <Domanda isHeld={quizdata.isHeld}
        //  toggle={()=>ChangeColor()}
          answer={provetta[4]}
           value={questionHtml[4]} />
    
    </div>
    
    ) 
}
