import React , { useState} from 'react'
import { nanoid } from 'nanoid'

export default function SetButtons(props){
  
    const [buttonArray , setButtonArray ] = useState(pushArray())

    function getArray(){
        return ( {id:nanoid() , isheld:false})
    }
    function pushArray(){
        const dice=[]
        for ( let i=0 ; i< 1 ; i++){
            dice.push(getArray())
        }
        return dice
    }
    function ChangeColor(id){
        setButtonArray(prev=> prev.map(get=>{
            return get.id === id ? {...get , isheld:!get.isheld} : get
        })
        )
    }
    const buttonFinal = buttonArray.map(get=>{
        const styles = { backgroundColor : get.isheld ? 'green' :  '#FFF'}
        const stylesCheck = {backgroundColor : props.correct ?  ' green' :  get.isheld === true ? 'red' :'rgb(221, 224, 238)'}
          return    <>
          {props.check ? 
                   <button className='main-btn' disabled style={stylesCheck} id={props.id}  onClick={props.toggle}>{props.value}</button>             
                 : <button className='main-btn' style={styles} id={props.id}  onClick={()=>ChangeColor(get.id)}>{props.value}</button> } 
          </>
        })
    return (
    <>
    {buttonFinal}
    </>)
}
